/* ==UserStyle==
@name           Google Calendar Dracula
@namespace      github.com/dracula/google-calendar
@version        2.0.0
@description    A dark theme for Google Calendar
@author         accountingnerd [(https://github.com/accountingnerd)]
@homepageURL	https://github.com/dracula/google-calendar
==/UserStyle== */

@-moz-document domain("calendar.google.com")
{
	/* Insert code here... */
	:root
	{
		color-scheme: dark;
		--background: #282a36;
		--currentLine: #44475a;
		--foreground: #f8f8f2;
		--comment: #6272a4;
		--cyan: #8be9fd;
		--green: #50fa7b;
		--orange: #ffb86c;
		--pink: #ff79c6;
		--purple: #bd93f9;
		--red: #ff5555;
		--yellow: #f1fa8c;
	}

	body > div
	{
		background-color: var(--background);
		color: var(--foreground);
	}

	header
	{
		background-color: var(--background)!important;
	}

	header div span
	{
		color: var(--foreground)!important;
	}

	header svg path,
	header svg circle,
	header button div,
	header div,
	header i,
	/* Time Insights */
	div[jsname = hzkHAc] div,
	div[role = complementary] button div
	{
		color: var(--foreground)!important;
	}


	div[jsname = hzkHAc] div > div > div > div
	{
		color: var(--comment)!important;
	}

	div[jsname = hzkHAc] span svg,
	div[jsname = hzkHAc] span
	{
		color: var(--purple);
	}

	header form button svg path
	{
		color: var(--purple)!important;
	}

	header form,
	header form div
	{
		background-color: var(--currentLine)!important;
	}


	#drawerMiniMonthNavigator,
	/* Month Name */
	#drawerMiniMonthNavigator > div > div > span,
	/* Current Month Days */
	#drawerMiniMonthNavigator table td > button
	{
		color: var(--foreground);
	}

	.hEtGGf::before,
	.hEtGGf::after
	{
		display: none!important;
	}

	/* Day Headings */
	#drawerMiniMonthNavigator table th > span > div
	{
		color: var(--pink);
	}

	/* Other Month Days */
	#drawerMiniMonthNavigator table td.q2d9Ze > button
	{
		color: var(--comment);
	}

	li[role = listitem],
	li[role = listitem] div > span
	{
		color: var(--foreground);
	}

	div[role = search] div
	{
		color: var(--pink)
	}

	div[role = grid],
	div[role = grid] div[role = row],
	div[role = grid] div.SrADx div,
	/* Time Div */
	div.lqYlwe > div,
	ul[role = list]
	{
		background-color: var(--background)!important;
		color: var(--foreground);
	}

	/* Time Div */
	div.lqYlwe > div span
	{
		color: var(--foreground);
	}

	div[data-chips-dialog = true] span,
	div[data-chips-dialog = true] svg,
	div[data-chips-dialog = true] button,
	div[data-chips-dialog = true] i,
	div[data-chips-dialog = true] .google-material-icons,
	div[data-chips-dialog = true] .AzuXid,
	ul[role = menu] li,
	ul[role = menu] i,
	div[role = menu] > div,
	ul[role = menu],
	div[role = menuitemradio] div,
	div[role = menuitemradio] i.google-material-icons
	{
		color: var(--foreground);
	}

	div[data-chips-dialog = true] a,
	div[data-chips-dialog = true] i > a
	{
		color: var(--cyan)!important;
	}

	div[data-chips-dialog = true] ol > li,
	div[data-chips-dialog = true] .toUqff
	{
		color: var(--purple);
	}

	div[data-chips-dialog = true] input[type = text] *,
	div[data-chips-dialog = true] > span.kma42e label > span *,
	div[data-chips-dialog = true] > span.kma42e > div,
	/* Handle for Pop-up Calendar Add */
	div[data-chips-dialog = true] div.ChfiMc
	{
		color: var(--foreground);
	}

	div[data-chips-dialog = true] button > div
	{
		color: var(--foreground)
	}

	div[data-chips-dialog = true] ul > li span span,
	div[data-chips-dialog = true] ul > li
	{
		color: var(--foreground)!important;
	}

	div[data-chips-dialog = true] label > span,
	div[data-chips-dialog = true] label > div,
	div[data-chips-dialog = true] label > input
	{
		color: var(--foreground)!important;
	}

	div[data-chips-dialog = true] button[role = tab] div span,
	div[data-chips-dialog = true] button[role = tab] div
	{
		color: var(--purple)
	}

	div[role = "dialog"] > span > div > div
	{
		background-color: var(--currentLine);
	}

	div[data-chips-dialog = true]
	{
		border: 2px solid var(--pink);
		background-color: var(--currentLine) color: var(--currentLine)
	}

	div[role = "radiogroup"] label
	{
		color: var(--foreground)
	}

	div[role = combobox] div,
	div[role = combobox] div,
	div[role = combobox] span
	{
		color: var(--foreground)
	}

	div[role = presentation] h2 > div[role = link],
	.tWjOu,
	.tWjOu.pCcXPe,
	.tWjOu.N4XV7d
	{
		color: var(--foreground);
	}

	div[role = presentation] h2 > div[role = link]:hover
	{
		background-color: var(--purple);
	}


	/* Current Date - Week View */
	.tWjOu.F262Ye
	{
		color: var(--purple);
	}
	.nSCxEf.F262Ye
	{
		background-color: var(--purple);
	}

	/* Week View - Date Hover */
	.nSCxEf.RKLVef:hover,
	body.geSgge .nSCxEf.RKLVef:hover,
	.nSCxEf.N4XV7d.RKLVef:hover,
	body.geSgge .nSCxEf.N4XV7d.RKLVef:hover,
	.nSCxEf.F262Ye.RKLVef:hover,
	body.geSgge .nSCxEf.F262Ye.RKLVef:hover
	{
		background-color: var(--pink);
	}

	/* Week View -- Top Grid Line */
	.oXZ1yb.T8M5bd .uEzZIb::before,
	.oXZ1yb.T8M5bd .uEzZIb::after,
	.oXZ1yb.T8M5bd .UEmuKf::before
	{
		border-top: 1px solid var(--foreground);
	}
}


