/* ==UserStyle==
@name Gmail Catppuccin
@namespace github.com/catppuccin/userstyles/styles/gmail
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/gmail
@version 2025.01.15
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/gmail/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Agmail
@description Soothing pastel theme for Gmail
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("mail.google.com") {
  :root {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    @crust-filter: @catppuccin-filters[@@flavor][@crust];
    @text-filter: @catppuccin-filters[@@flavor][@text];
    @yellow-filter: @catppuccin-filters[@@flavor][@yellow];

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }

      caret-color: @text;
    }

    #input-styling {
      input,
      select,
      button {
        background-color: @mantle;
        color: @text;
        border-color: @surface0;
        border-style: solid;
        border-width: 1px;
        box-shadow: none;
      }
    }

    * {
      text-shadow: none !important;
    }

    /* Gmail logo */
    [src="https://ssl.gstatic.com/ui/v1/icons/mail/rfr/logo_gmail_lockup_default_1x_r5.png"],
    [src="https://ssl.gstatic.com/ui/v1/icons/mail/rfr/logo_gmail_lockup_dark_1x_r5.png"] {
      height: unset !important;
      width: unset !important;
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" viewBox="17.727 14.319 30 22.5" width="30" height="22.5"><path fill="@{blue}" d="M19.773 36.819h4.773V25.227l-6.819-5.114v14.659a2.044 2.044 0 0 0 2.045 2.045"/><path fill="@{green}" d="M40.909 36.819h4.773a2.044 2.044 0 0 0 2.045-2.045v-14.66l-6.819 5.114"/><path fill="@{yellow}" d="M40.909 16.364v8.864l6.819-5.114v-2.727c0-2.529-2.888-3.971-4.909-2.455"/><path fill="@{red}" d="M24.545 25.227v-8.863l8.181 6.136 8.181-6.136v8.864l-8.181 6.136m-15-13.977v2.727l6.819 5.114v-8.864l-1.909-1.431c-2.025-1.517-4.91-.075-4.91 2.455"/></svg>'
      );
      content: url("data:image/svg+xml,@{svg}");
    }

    /* Tooltips */
    .T-ays-iP,
    .gb_Ac {
      background-color: @crust;
      color: @text;
    }

    /* Buttons */

    /* Solid primary */
    .T-I-atl {
      background-color: @accent;
      color: @crust;
    }

    /* Transparent primary */
    .l0,
    div.b8 .a8k,
    .bAo > .ad {
      color: @accent;

      &::before {
        background-color: @accent;
      }
    }

    /* Transparent secondary */
    .bs0 > .acM,
    .bti > .btg,
    .a8Y > .T-I-ax7 {
      color: @text;

      /* Disabled */
      &.acN {
        color: @subtext0;
      }

      &:hover::before {
        background-color: @overlay2;
      }
    }

    /* Header and sidebars */
    .w-asV,
    .bkL,
    .bhZ.bym,
    .bhZ.bjB,
    .bhZ.bym.baA {
      background-color: @mantle;
    }
    /* Header icons */
    .gb_Kc svg,
    .gb_Pc.gb_Uc svg,
    .gb_Lc .gb_rd .gb_sd,
    .gb_Lc .gb_rd .gb_Kc,
    .gb_Lc .gb_rd .gb_ud,
    .gb_Pc.gb_Uc .gb_sd {
      color: @text;
    }
    /* Search mail input */
    .gb_Kc .gb_hd {
      background-color: @surface1;

      .gb_je,
      .gb_qe,
      .gsan_a,
      .gsas_a,
      svg {
        color: @text;
      }
    }
    /* Search mail input on focus */
    .gb_hd.gb_id {
      background-color: @base;
    }
    /* Active search / filter modal */
